mapper.select:
select
  admin_user_id, token, update_time, expire_time
from tb_newbee_mall_admin_user_token


        and admin_user_id = #{adminUserId}


        and token = #{token}


        and update_time = #{updateTime}


        and expire_time = #{expireTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from tb_newbee_mall_admin_user_token


        and admin_user_id = #{adminUserId}


        and token = #{token}


        and update_time = #{updateTime}


        and expire_time = #{expireTime}
